script:
let gameBoard = ['', '', '', '', '', '', '', '', ''];
let currentPlayer = 'X';
let gameActive = true;
let scores = { 'X': 0, 'O': 0, 'Draw': 0 };

const winningCombinations = [
    [0, 1, 2], [3, 4, 5], [6, 7, 8], 
    [0, 3, 6], [1, 4, 7], [2, 5, 8], 
    [0, 4, 8], [2, 4, 6]            
];

const statusDisplay = document.getElementById('status');
const scoreXDisplay = document.getElementById('score-x');
const scoreODisplay = document.getElementById('score-o');
const scoreDrawDisplay = document.getElementById('score-draw');
const cells = document.querySelectorAll('.cell');
const resetButton = document.getElementById('reset-button');

const initialMessage = () => `Player <span class="${currentPlayer}">${currentPlayer}</span>'s turn`;

function updateScoresDisplay() {
    scoreXDisplay.textContent = scores.X;
    scoreODisplay.textContent = scores.O;
    scoreDrawDisplay.textContent = scores.Draw;
}

function handleResultValidation() {
    let roundWon = false;
    for (let i = 0; i < winningCombinations.length; i++) {
        const winCondition = winningCombinations[i];
        let a = gameBoard[winCondition[0]];
        let b = gameBoard[winCondition[1]];
        let c = gameBoard[winCondition[2]];

        if (a === '' || b === '' || c === '') {
            continue;
        }
        if (a === b && b === c) {
            roundWon = true;
            // Highlight winning cells
            winCondition.forEach(index => {
                cells[index].classList.add('winning-cell');
            });
            break;
        }
    }

    if (roundWon) {
        scores[currentPlayer]++;
        statusDisplay.innerHTML = `Player <span class="${currentPlayer}">${currentPlayer}</span> wins!`;
        gameActive = false;
        updateScoresDisplay();
        return;
    }

    // Check for Draw
    if (!gameBoard.includes('')) {
        scores.Draw++;
        statusDisplay.innerHTML = `Game ended in a <span class="Draw">Draw</span>!`;
        gameActive = false;
        updateScoresDisplay();
        return;
    }

    // If game continues, switch player
    handlePlayerChange();
}

function handlePlayerChange() {
    currentPlayer = currentPlayer === 'X' ? 'O' : 'X';
    statusDisplay.innerHTML = initialMessage();
}

function handleCellPlayed(clickedCell, clickedCellIndex) {
    gameBoard[clickedCellIndex] = currentPlayer;
    // Ensure the span tag is used for styling the content
    clickedCell.innerHTML = `<span class="${currentPlayer}">${currentPlayer}</span>`;
}

function handleCellClick(event) {
    const clickedCell = event.target;
    // Get the index from the data attribute
    const clickedCellIndex = parseInt(clickedCell.dataset.index);

    // Guard clauses
    if (isNaN(clickedCellIndex) || gameBoard[clickedCellIndex] !== '' || !gameActive) {
        return;
    }

    handleCellPlayed(clickedCell, clickedCellIndex);
    handleResultValidation();
}

function handleRestartGame() {
    gameActive = true;
    gameBoard = ['', '', '', '', '', '', '', '', ''];
    // Keep the current player the same, or implement alternating start if desired. Sticking to simple restart.
    // We reset currentPlayer to X for consistency of round start.
    currentPlayer = 'X'; 
    statusDisplay.innerHTML = initialMessage();

    cells.forEach(cell => {
        cell.innerHTML = '';
        cell.classList.remove('winning-cell');
    });
}

// Initialization and Event Listeners
document.addEventListener('DOMContentLoaded', () => {
    // Initial setup display
    statusDisplay.innerHTML = initialMessage();
    updateScoresDisplay();

    // Attach listeners to cells
    cells.forEach(cell => {
        cell.addEventListener('click', handleCellClick);
    });

    // Attach listener to reset button
    resetButton.addEventListener('click', handleRestartGame);
});
